     'This Month'  : [moment().startOf('month'), moment().endOf('month')],          'Last Month'  : [moment().subtract(1, 'month').startOf('month'), moment().subtract(1, 'month').endOf('month')]        },        startDate: moment().subtract(29, 'days'),        endDate  : moment()      },      function (start, end) {        $('#daterange-btn span').html(start.format('MMMM D, YYYY') + ' - ' + end.format('MMMM D, YYYY'))      }    )    //Date picker    $('#datepicker').datepicker({      autoclose: true,	  format: 'dd MM yyyy',	  orientation: "bottom auto"    })    //iCheck for checkbox and radio inputs    $('input[type="checkbox"].minimal, input[type="radio"].minimal').iCheck({      checkboxClass: 'icheckbox_minimal-blue',      radioClass   : 'iradio_minimal-blue'    })    //Red color scheme for iCheck    $('input[type="checkbox"].minimal-red, input[type="radio"].minimal-red').iCheck({      checkboxClass: 'icheckbox_minimal-red',      radioClass   : 'iradio_minimal-red'    })    //Flat red color scheme for iCheck    $('input[type="checkbox"].flat-red, input[type="radio"].flat-red').iCheck({      checkboxClass: 'icheckbox_flat-green',      radioClass   : 'iradio_flat-green'    })    //Colorpicker    $('.my-colorpicker1').colorpicker()    //color picker with addon    $('.my-colorpicker2').colorpicker()    //Timepicker    $('.timepicker').timepicker({      showInputs: false    })  })</script></body></html>